import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

function Users() {
    const [users, setUsers] = useState([])

    useEffect(() => {
        axios.get('http://localhost:3001')
        .then(result => setUsers(result.data))
        .catch(err => console.log(err))
    }, [])

    const handleDelete = (id) => {
        axios.delete('http://localhost:3001/deleteUser/'+id)
        .then(res => {
            console.log(res)
            window.location.reload()
        })
        .catch(errr => console.log(errr))
    }

  return (
    <div>
        <div>
            <Link to="/create" >Add +</Link>      
                <div>
                    {
                    users.map((user) => {
                        return (
                        <div>
                            <h3>Marque</h3><p>{user.marque}</p>
                            <h3>Modèle</h3><p>{user.modele}</p>
                            <h3>Prix</h3><p>{user.prix}</p>
                            <h3>Boîte de Vitesse</h3><p>{user.vitesse}</p>
                            <h3>Année de sortie</h3><p>{user.annee}</p>
                            <h3>Nb de porte</h3><p>{user.porte}</p>
                            <h3>Carburant</h3><p>{user.carburant}</p>
                            <h3>Url</h3><p>{user.url}</p>
                            <div>
                                <Link to={`/update/${user._id}`}>Update</Link>
                                <button onClick={(e) => handleDelete(user._id)}>
                                    Delete
                                </button>
                            </div>
                        </div>
                        )
                    })
                    }
                </div>
        </div>
    </div>
  )
}

export default Users
