import React, { useState } from 'react'

function HomeClient() {
    const [list, setList] = useState()
    const [search, setSearch] = useState()
    const [users, setUsers] = useState([])

    useEffect(() => {
        axios.get('http://localhost:3001')
        .then(result => setUsers(result.data))
        .catch(err => console.log(err))
    }, [])

    const onChange = (list) => {
        setSearch(list.target.search)
    }
    const onSearch= (searchTerm) => {
        setSearch(searchTerm);
    }


  return (
    <div>
        <form className='recherche' action="">
                <input type="text" 
                placeholder='Recherche ...'
                onChange={(e) => setSearch(e.target.value)}
                value={search} onChanges={onChange}
                />
                <button onClick={()=> onSearch(search)}>Search</button>  
            </form>
                <div className="dropdown">
                    {users && users.filter(list => {
                        const searchTerm = search;
                        const title = list.title;

                        return searchTerm && marque.startsWith(searchTerm) && marque !== searchTerm;
                    })
                    .map((list)=> (
                    <div onClick={() => onSearch(list.title)} className='dropdown-row' key={list.title}>
                        {list.title}
                    </div> ))}
                </div>
                <div className="container">
                    <div className="grid">
                {
                list && list.filter((list) => {
                    return search === ''
                    ? list
                    : list.title.includes(search)
                }).map((list) => {
                    return (    
                        <div>

                        </div>   

                    )
                })
            }
                    </div>
                </div>

    </div>
  )
}

export default HomeClient
